/// <summary>
/// Configuration class used to manage system settings and label translation for the invoice approvals mobile application.
/// </summary>
[DataContractAttribute('VendMobileInvoiceApprovalConfig')]
public class VendMobileInvoiceApprovalConfig
{
    boolean isMatchingEnabled;
    boolean isTotalsEnabled;
    str approveLabel, acceptLabel, completeLabel, rejectLabel, delegateLabel, requestChangeLabel, resubmitLabel, recallLabel;
    str distributionsLabel, attachmentsLabel, moreLabel, newVersionLabel, newVersionDescriptionLabel;

}

Method.Source:
/// <summary>
/// Instantiates a new instance of <c>VendMobileInvoiceApprovalConfig</c>
/// </summary>
protected void new()
{
    super();
}

/// <summary>
/// Creates and initializes a new instance of <c>VendMobileInvoiceApprovalConfig</c>.
/// </summary>
/// <returns>Initialized instance of the config object.</returns>
public static VendMobileInvoiceApprovalConfig construct()
{
    VendParameters vendParameters = VendParameters::find();

    VendMobileInvoiceApprovalConfig config = new VendMobileInvoiceApprovalConfig();

    config.invoiceMatchingEnabled(vendParameters.editUseInvoiceMatching());
    config.invoiceTotalsEnabled(vendParameters.editCalculateInvoiceTotal());
    config.approveLabel(new MenuFunction(menuItemActionStr(VendInvoiceApprovalApprove), MenuItemType::Action).label());
    config.acceptLabel(new MenuFunction(menuItemActionStr(WorkflowClaimWorkItem), MenuItemType::Action).label());
    config.completeLabel(new MenuFunction(menuItemActionStr(VendInvoiceTaskComplete), MenuItemType::Action).label());
    config.rejectLabel(new MenuFunction(menuItemActionStr(VendInvoiceApprovalReject), MenuItemType::Action).label());
    config.requestChangeLabel("@SYS108711");
    config.resubmitLabel(new MenuFunction(menuItemActionStr(VendInvoiceHeaderWorkflowReSubmit), MenuItemType::Action).label());
    config.delegateLabel(new MenuFunction(menuItemActionStr(VendInvoiceTaskDelegate), MenuItemType::Action).label());
    config.recallLabel(new MenuFunction(menuItemActionStr(VendInvoiceHeaderWorkflowCancel), MenuItemType::Action).label());
    config.distributionsLabel("@SourceDocumentation:Distributions");
    config.attachmentsLabel("@AccountsPayableMobile:Attachments");
    config.moreLabel("@AccountsPayableMobile:More");
    config.newVersionLabel("@AccountsPayableMobile:NewVersion");
    config.newVersionDescriptionLabel("@AccountsPayableMobile:NewVersionDescription");

    return config;
}

/// <summary>
/// Gets the name of the config class contract attribute.
/// </summary>
/// <returns>The attribute name of the contract.</returns>
public static Description getConfigName()
{
    DictClass dictClass = new DictClass(classNum(VendMobileInvoiceApprovalConfig));
    DataContractAttribute attribute = dictClass.getAttribute(classStr(DataContractAttribute));
    return attribute.Name();
}

/// <summary>
/// Returns whether or not invoice matching is enabled in the system.
/// </summary>
/// <param name = "_isMatchingEnabled">Is matching enabled.</param>
/// <returns>true if invoice matching is enabled.</returns>
[DataMemberAttribute('InvoiceMatchingEnabled')]
public boolean invoiceMatchingEnabled(boolean _isMatchingEnabled = isMatchingEnabled)
{
    isMatchingEnabled = _isMatchingEnabled;
    return isMatchingEnabled;
}

