Method.Source:
/// <summary>
/// Gets the workspace metadata instance.
/// </summary>
/// <returns>An instance of <c>SysAppWorkspaceMetadata</c>.</returns>
public SysAppWorkspaceMetadata getWorkspaceMetadata()
{
    SysAppWorkspaceMetadata metadata = super();

    metadata.workspaceTitle("@AccountsPayableMobile:InvoiceApprovals");
    metadata.workspaceDescription("@AccountsPayableMobile:InvoicesForApproval");

    this.addSettings(metadata);

    return metadata;
}

private void addSettings(SysAppWorkspaceMetadata _metadata)
{
    VendMobileInvoiceApprovalConfig config = VendMobileInvoiceApprovalConfig::construct();

    _metadata.addConfig(VendMobileInvoiceApprovalConfig::getConfigName(), config);
}
