Method.Source:
/// <summary>
/// Update the mobile workflow status for the current document in order to reflect a state change for the invoice for the
/// invoice approval mobile application.
/// </summary>
/// <param name="args">Pre/Post event handlers.</param>
[PostHandlerFor(formStr(VendorInvoiceWorkflowDropDialog), formMethodStr(VendorInvoiceWorkflowDropDialog, updateWorkflowControls))]
public static void VendorInvoiceWorkflowDropDialog_Post_updateWorkflowControls(XppPrePostArgs args)
{
    if (SysAppMobile::isMobileMode())
    {
        FormRun workflowDropDialog = args.getThis() as FormRun;
        WorkflowWorkItemTable workflowWorkItemTable = workflowDropDialog.getActiveWorkflowWorkItem() as WorkflowWorkItemTable;

        if (WorkflowWorkItemTable)
        {
            VendInvoiceInfoTable vendInvoiceInfoTable;

            vendInvoiceInfoTable.skipDataMethods(true);

            update_recordset vendInvoiceInfoTable
                setting MobileWorkflowStatus = VendMobileInvoiceWorkflowStatus::Processed
                    where vendInvoiceInfoTable.RecId == workflowWorkItemTable.RefRecId;
        }
    }
}
