[Form]
public class VendMobileInvoiceHeaderDetails extends FormRun implements VendorInvoiceIWorkflow
{
}

private void updateWorkflowIconForHeader()
{
    //The workflow icon on header should be visible only if you can submit it to the workflow with an active workflow configuration
    //or if there are workitems on the invoice for the current user
    //or if there is a workflow history available for the invoice
    if ((this.canSubmitToWorkflow() && Workflow::findWorkflowConfigurationToActivate(vendInvoiceInfoTable, workflowTypeStr(VendProcessInvoice))
        || Workflow::findWorkItemsForCurrentUser(vendInvoiceInfoTable).elements()
        || Workflow::findTrackingStatusForDocument(vendInvoiceInfoTable)))
    {
        VendorInvoiceHeaderWorkflowDropDialog.visible(true);
    }
    else
    {
        VendorInvoiceHeaderWorkflowDropDialog.visible(false);
    }
}

[SysClientCacheDataMethod(true)]
public display VendInvoiceAttachmentCount attachmentCount()
{
    return DocuRef::getAttachmentCount(element);
}

public int active()
{
    int ret = super();

    element.updateWorkflowIconForHeader();

    return ret;
}

[SysClientCacheDataMethodAttribute(true)]
public display CurrencyCode AccountingCurrency()
{
    return Ledger::accountingCurrency();
}

/// <summary>
/// Display method used to determine whether or not to show the header distributions link on the invoice approval mobile app.
/// </summary>
/// <returns>A boolean indicating whether or not to show header distribtions</returns>
[SysClientCacheDataMethodAttribute(true)]
public display boolean showHeaderDistributions(VendInvoiceInfoTable _vendInvoiceInfoTable)
{
    return VendMobileInvoiceHeaderDistributionsView::hasHeaderDistributions(_vendInvoiceInfoTable);
}

public void init()
{
    super();

    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, packingSlipId));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, pdsCWUnitId));
    vendInvoiceInfoLine_ds.cacheAddMethod(tableMethodStr(VendInvoiceInfoLine, itemName));
}

/// <summary>
/// Used solely as part of the recording of the mobile application in order for the task recorder to recognize that we have done a save action
/// on our entity in order to properly fire events that are necessary on the mobile client.
/// </summary>
public void clicked()
{
    super();
    VendInvoiceInfoTable_ds.write();
}
