public void init()
{
    super();

    Args args = this.args();

    if (!args)
    {
        error("@SYS22539");
        this.close();
    }

    this.resetInvoiceMobileWorkflowStatuses();

    switch (args.menuItemName())
    {
        case menuitemDisplayStr(VendInvoiceInfoListPage):
            this.adjustWorkflowWorkItemTableRanges();
            break;
    }
}

private void adjustWorkflowWorkItemTableRanges()
{
    FormDataSource formDataSource = this.dataSource();
    QueryBuildDataSource qbdsWorkFlowWorkItemTable = formDataSource.query().dataSourceTable(tableNum(WorkflowWorkItemTable));
    QueryBuildRange userIdRange = qbdsWorkFlowWorkItemTable.addRange(fieldNum(WorkflowWorkItemTable, UserId));
    userIdRange.value(curUserId());
}

private void resetInvoiceMobileWorkflowStatuses()
{
    VendInvoiceInfoTable vendInvoiceInfoTableLocal;
    WorkflowWorkItemTable workflowWorkItemTableLocal;

    vendInvoiceInfoTableLocal.skipDataMethods(true);

    update_recordset vendInvoiceInfoTableLocal
        setting MobileWorkflowStatus = VendMobileInvoiceWorkflowStatus::Unprocessed
            where vendInvoiceInfoTableLocal.MobileWorkflowStatus != VendMobileInvoiceWorkflowStatus::Unprocessed
                && vendInvoiceInfoTableLocal.ParmJobStatus != ParmJobStatus::Executed
            join workflowWorkItemTableLocal
                where workflowWorkItemTableLocal.CompanyId == vendInvoiceInfoTableLocal.DataAreaId
                    && workflowWorkItemTableLocal.RefRecId == vendInvoiceInfoTableLocal.RecId
                    && workflowWorkItemTableLocal.RefTableId == tableNum(VendInvoiceInfoTable)
                    && workflowWorkItemTableLocal.Status == WorkflowWorkItemStatus::Pending
                    && workflowWorkItemTableLocal.UserId == curUserId();
}

/// <summary>
/// Display method used to determine whether or not to show the header distributions link on the invoice approval mobile app.
/// </summary>
/// <returns>A boolean indicating whether or not to show header distribtions</returns>
[SysClientCacheDataMethodAttribute(true)]
public display boolean showHeaderDistributions(VendInvoiceInfoTable _vendInvoiceInfoTable)
{
    VendMobileInvoiceHeaderDistributionsView vendMobileInvoiceHeaderDistributionsView;
    select firstonly InvoiceRecId from vendMobileInvoiceHeaderDistributionsView
        where vendMobileInvoiceHeaderDistributionsView.InvoiceRecId == _vendInvoiceInfoTable.RecId;

    return vendMobileInvoiceHeaderDistributionsView.InvoiceRecId != 0;
}
