/// <summary>
/// Populates the <c>LedgerMobileDimensionPrevie</c> table for a given <c>LedgerDimensionAccount</c> by taking each individual
/// segment, value, and description and inserting them into the table.
/// </summary>
/// <param name = "_ledgerMobileDimensionPreview">An instance of the <c>LedgerMobileDimensionPreview</c> temp table buffer.</param>
/// <param name = "_ledgerDimension"><c>LedgerDimensionAccount</c> value to break into segments and insert into the table</param>
public static void populateForLedgerDimension(LedgerMobileDimensionPreview _ledgerMobileDimensionPreview, LedgerDimensionAccount _ledgerDimension)
{
    DimensionAttribute dimensionAttribute;

    if (_ledgerDimension)
    {
        DimensionStorage dimStorage = DimensionStorage::findById(_ledgerDimension);

        _ledgerMobileDimensionPreview.clear();
        _ledgerMobileDimensionPreview.LedgerDimension = _ledgerDimension;

        for (Counter segmentCount = 1 ; segmentCount <= dimStorage.segmentCount(); segmentCount++)
        {
            DimensionStorageSegment dimStorageSegment = dimStorage.getSegment(segmentCount);

            if (dimStorageSegment)
            {
                if (dimStorageSegment.isEmpty())
                {
                    DimensionHierarchyId dimensionHierarchyId = dimStorage.getHierarchyIdForSegment(segmentCount);
                    DimensionHierarchyLevel dimensionHierarchyLevel = DimensionHierarchyLevel::findByDimensionHierarchyAndLevel(dimensionHierarchyId, segmentCount);

                    dimensionAttribute = DimensionAttribute::find(DimensionHierarchyLevel.DimensionAttribute);

                    _ledgerMobileDimensionPreview.DimensionAttribute = DimensionHierarchyLevel.DimensionAttribute;
                    _ledgerMobileDimensionPreview.DimensionAttributeValue = 0;
                    _ledgerMobileDimensionPreview.Value = '';
                    _ledgerMobileDimensionPreview.Description = '';
                }
                else
                {
                    dimensionAttribute = DimensionAttribute::find(dimStorageSegment.parmDimensionAttributeId());

                    DimensionAttributeValue dimensionAttributeValue = DimensionAttributeValue::find(dimStorageSegment.parmDimensionAttributeValueId());

                    _ledgerMobileDimensionPreview.DimensionAttribute = dimStorageSegment.parmDimensionAttributeId();
                    _ledgerMobileDimensionPreview.DimensionAttributeValue = dimStorageSegment.parmDimensionAttributeValueId();

                    _ledgerMobileDimensionPreview.Value = dimStorageSegment.parmDisplayValue();
                    _ledgerMobileDimensionPreview.Description = dimensionAttributeValue.getName();
                }

                _ledgerMobileDimensionPreview.Ordinal = segmentCount;

                // Use the name of the attribute directly for all other types (main account, custom list, existing list)
                _ledgerMobileDimensionPreview.Name = dimensionAttribute.localizedName();

                _ledgerMobileDimensionPreview.insert();
            }
        }
    }
}
