/// <summary>
/// Populates the provided buffer with descriptions for all the records linked to the specified header and line.
/// </summary>
/// <param name = "_vendMobileInvoiceDistributionTree">An instance of the <c>VendMobileInvoiceDistributionTree</c> temporary buffer to populate.</param>
/// <param name = "_header">RecId of the <c>SourceDocumentHeader</c> that requires the data.</param>
/// <param name = "_line">RecId of the <c>SourceDocumentLine</c> that requires the data. All lines are included if the value is 0.</param>
/// <param name = "_processParentNodes">Controls whether parent nodes building the tree structure should also be created.</param>
/// <param name = "_tableToSkip">When specified, forces all source document lines linked to a record of a given <c>TableId</c> to be ignored.</param>
public static void populateTempBuffer(VendMobileInvoiceDistributionTree _vendMobileInvoiceDistributionTree,
    SourceDocumentHeaderRefRecId _header,
    SourceDocumentLineRefRecId _line,
    boolean _processParentNodes,
    TableId _tableToSkip = 0)
{
    VendMobileInvoiceAllDistributionsView distributionWithParents;
    SourceDocumentLine sourceDocumentLine, rootSourceDocumentLine;
    RefRecId previousSourceDocumentLine, previousAccountingDistributionRecId;
    DimensionAttributeValueCombination dimensionAttributeValueCombination;
    SourceDocumentAmountFormTreeBuildHelper helper;

    str previousDescription;
    boolean differentSourceDocumentLines;

    ttsbegin;

    while select distributionWithParents
        order by distributionWithParents.HeaderLine, distributionWithParents.L1SourceDocumentLine, distributionWithParents.L2SourceDocumentLine, 
            distributionWithParents.L3SourceDocumentLine, distributionWithParents.AccountingDistributionRecId
            where distributionWithParents.SourceDocumentHeader == _header
               && (_line == 0 || distributionWithParents.L1SourceDocumentLine == _line)
        join SourceDocumentLine
            where distributionWithParents.SourceDocumentLineRecId == sourceDocumentLine.RecId
               && sourceDocumentLine.SourceDocumentHeader == _header
        join TableId from rootSourceDocumentLine
            where distributionWithParents.L1SourceDocumentLine == rootSourceDocumentLine.RecId
               && rootSourceDocumentLine.SourceDocumentHeader == _header
               && rootSourceDocumentLine.SourceRelationType != _tableToSkip
        outer join dimensionAttributeValueCombination
            where distributionWithParents.LedgerDimension == dimensionAttributeValueCombination.RecId
    {
        if (distributionWithParents.AccountingDistributionRecId != previousAccountingDistributionRecId)
        {
            SourceDocumentLineItem sourceDocumentLineItem = SourceDocumentLineItem::newFromSourceDocumentLine(sourceDocumentLine);

            SourceDocumentAmount sourceDocumentAmount = sourceDocumentLineItem.parmSourceDocumentAmountMap().lookup(distributionWithParents.MonetaryAmount);

            if (!helper)
            {
                helper = SourceDocumentAmountFormTreeBuildHelper::construct();
            }

            helper.parmSourceDocumentAmount(sourceDocumentAmount);

            differentSourceDocumentLines = (SourceDocumentLine.RecId != previousSourceDocumentLine);

            str currentDescription = VendMobileInvoiceDistributionTree::populateForCurrentAccountingDistribution(_vendMobileInvoiceDistributionTree,
                (differentSourceDocumentLines && _processParentNodes),
                distributionWithParents,
                dimensionAttributeValueCombination.DisplayValue,
                helper,
                previousDescription);

            if (differentSourceDocumentLines)
            {
                previousDescription = '';
            }
            else if (currentDescription != '')
            {
                previousDescription = currentDescription;
            }

            previousSourceDocumentLine = sourceDocumentLine.RecId;
            previousAccountingDistributionRecId = distributionWithParents.AccountingDistributionRecId;
        }
    }

    ttscommit;
}

private static str populateForCurrentAccountingDistribution(VendMobileInvoiceDistributionTree _vendMobileInvoiceDistributionTree,
    boolean _processParentNodes,
    VendMobileInvoiceAllDistributionsView _vendMobileInvoiceAllDistributionsView,
    DimensionDisplayValue _dimensionDisplayValue,
    SourceDocumentAmountFormTreeBuildHelper _helper,
    str _previousDescription = '')
{
    str returnValue = _previousDescription;

    if (_processParentNodes)
    {
        if (_helper.needToCreateParentNode())
        {
            str currentDescription = _helper.getParentNodeLabel();

            _vendMobileInvoiceDistributionTree.clear();
            _vendMobileInvoiceDistributionTree.InvoiceRecId = _vendMobileInvoiceAllDistributionsView.InvoiceRecId;
            _vendMobileInvoiceDistributionTree.AccountingDistribution = _vendMobileInvoiceAllDistributionsView.AccountingDistributionRecId;
            _vendMobileInvoiceDistributionTree.SourceDocumentLine = _vendMobileInvoiceAllDistributionsView.SourceDocumentLineRecId;
            _vendMobileInvoiceDistributionTree.TopLevelSourceDocumentLine = _vendMobileInvoiceAllDistributionsView.L1SourceDocumentLine;
            _vendMobileInvoiceDistributionTree.Description = currentDescription;
            _vendMobileInvoiceDistributionTree.TreeNodeType = VendMobileInvoiceDistributionTreeType::ParentNode;
            _vendMobileInvoiceDistributionTree.insert();
        }

        if (_helper.needToCreateSubNode())
        {
            str currentDescription = _helper.getSubNodeLabel();

            _vendMobileInvoiceDistributionTree.clear();
            _vendMobileInvoiceDistributionTree.InvoiceRecId = _vendMobileInvoiceAllDistributionsView.InvoiceRecId;
            _vendMobileInvoiceDistributionTree.AccountingDistribution = _vendMobileInvoiceAllDistributionsView.AccountingDistributionRecId;
            _vendMobileInvoiceDistributionTree.SourceDocumentLine = _vendMobileInvoiceAllDistributionsView.SourceDocumentLineRecId;
            _vendMobileInvoiceDistributionTree.TopLevelSourceDocumentLine = _vendMobileInvoiceAllDistributionsView.L1SourceDocumentLine;
            _vendMobileInvoiceDistributionTree.Description = currentDescription;
            _vendMobileInvoiceDistributionTree.TreeNodeType = VendMobileInvoiceDistributionTreeType::SubNode;
            _vendMobileInvoiceDistributionTree.insert();
        }

        str currentDescription = _helper.getNodeLabel();

        if (_previousDescription != currentDescription)
        {
            returnValue = currentDescription;
            _vendMobileInvoiceDistributionTree.clear();
            _vendMobileInvoiceDistributionTree.InvoiceRecId = _vendMobileInvoiceAllDistributionsView.InvoiceRecId;
            _vendMobileInvoiceDistributionTree.AccountingDistribution = _vendMobileInvoiceAllDistributionsView.AccountingDistributionRecId;
            _vendMobileInvoiceDistributionTree.SourceDocumentLine = _vendMobileInvoiceAllDistributionsView.SourceDocumentLineRecId;
            _vendMobileInvoiceDistributionTree.TopLevelSourceDocumentLine = _vendMobileInvoiceAllDistributionsView.L1SourceDocumentLine;
            _vendMobileInvoiceDistributionTree.Description = currentDescription;
            _vendMobileInvoiceDistributionTree.TreeNodeType = VendMobileInvoiceDistributionTreeType::DescriptionNode;
            _vendMobileInvoiceDistributionTree.insert();
        }
    }

    str currentDescription = strFmt('%1: %2', _helper.getNodeLabel(true), _vendMobileInvoiceAllDistributionsView.TransactionCurrencyAmount);

    _vendMobileInvoiceDistributionTree.clear();
    _vendMobileInvoiceDistributionTree.InvoiceRecId = _vendMobileInvoiceAllDistributionsView.InvoiceRecId;
    _vendMobileInvoiceDistributionTree.AccountingDistribution = _vendMobileInvoiceAllDistributionsView.AccountingDistributionRecId;
    _vendMobileInvoiceDistributionTree.SourceDocumentLine = _vendMobileInvoiceAllDistributionsView.SourceDocumentLineRecId;
    _vendMobileInvoiceDistributionTree.TopLevelSourceDocumentLine = _vendMobileInvoiceAllDistributionsView.L1SourceDocumentLine;
    _vendMobileInvoiceDistributionTree.Description = currentDescription;
    _vendMobileInvoiceDistributionTree.AmountForDisplay = strFmt('%1', _vendMobileInvoiceAllDistributionsView.TransactionCurrencyAmount);
    _vendMobileInvoiceDistributionTree.TransactionCurrency = _vendMobileInvoiceAllDistributionsView.TransactionCurrency;
    _vendMobileInvoiceDistributionTree.LedgerDimension = _vendMobileInvoiceAllDistributionsView.LedgerDimension;
    _vendMobileInvoiceDistributionTree.DimensionDisplayValue = _dimensionDisplayValue;
    _vendMobileInvoiceDistributionTree.TreeNodeType = VendMobileInvoiceDistributionTreeType::DistributionNode;
    _vendMobileInvoiceDistributionTree.insert();

    return returnValue;
}
