/// <summary>
/// Generates SQL to indicate whether or not a record is a header or a line level accounting distribution.
/// </summary>
/// <returns>A sql string representing a column to indicate whether or not this is a header or line distribution.</returns>
public static str HeaderLine()
{
    /*
    Actual SQL generated by the code below for reference

    CASE
        WHEN T4.SourceDocumentLineRecId IS NULL
        THEN 1
        ELSE 0 END
    */

    const str t4PrefixWithReplacement = 'T4.%1';

    str ifNotHeader = SysComputedColumn::isNullExpression(strFmt(t4PrefixWithReplacement, fieldStr(VendMobileInvoiceHeaderDistributionsView, SourceDocumentLineRecId)));

    str caseStatement = SysComputedColumn::if(ifNotHeader, '1', '0');

    return caseStatement;
}

/// <summary>
/// Generates SQL to indicate which <c>SourceDocumentLine</c> is the Level 1 value for the data representing a tree for accounting distributions.
/// </summary>
/// <returns>A sql string representing a column to indicate which <c>SourceDocumentLine</c> is the L1 value.</returns>
public static str L1SourceDocumentLine()
{
    /*
    Actual SQL generated by the code below for reference

    CASE
        WHEN T4.SourceDocumentLineRecId IS NULL
        THEN T5.L1SourceDocumentLine
        ELSE T4.L1SourceDocumentLine END
    */

    const str t4PrefixWithReplacement = 'T4.%1';
    const str t5PrefixWithReplacement = 'T5.%1';

    str ifNotHeader = SysComputedColumn::isNullExpression(strFmt(t4PrefixWithReplacement, fieldStr(VendMobileInvoiceHeaderDistributionsView, SourceDocumentLineRecId)));
    str t5L1SourceDocumentLine = strFmt(t5PrefixWithReplacement, fieldStr(VendMobileInvoiceLineAllDistributionsView, L1SourceDocumentLine));
    str t4L1SourceDocumentLine = strFmt(t4PrefixWithReplacement, fieldStr(VendMobileInvoiceHeaderDistributionsView, L1SourceDocumentLine));

    str caseStatement = SysComputedColumn::if(ifNotHeader, t5L1SourceDocumentLine, t4L1SourceDocumentLine);

    return caseStatement;
}

